// WorkerPage.js

import React, { useState } from 'react';

const WorkerPage = () => {
  // State to store the search query
  const [searchQuery, setSearchQuery] = useState('');
  
  // Dummy data for employees (replace it with actual data)
  const [employees, setEmployees] = useState([
    { id: 1, name: 'John Doe', dob: '1990-01-01', role: 'Developer' },
    { id: 2, name: 'Jane Smith', dob: '1985-05-15', role: 'Designer' },
    // Add more employee data as needed
  ]);

  // Function to handle search input change
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Function to filter employees based on search query
  const filteredEmployees = employees.filter(employee =>
    employee.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      <h2>Worker Page</h2>
      <input 
        type="text" 
        placeholder="Search by name..." 
        value={searchQuery} 
        onChange={handleSearchChange} 
      />
      <div>
        {filteredEmployees.map(employee => (
          <div key={employee.id}>
            <p>Emp ID: {employee.id}</p>
            <p>Name: {employee.name}</p>
            <p>DOB: {employee.dob}</p>
            <p>Role: {employee.role}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default WorkerPage;
